                <a href="#" onclick="shareOnWhatsApp(); return false;"><i aria-hidden="true"></i> WhatsApp</a>
            </div> -->
        </div>

        <!-- Sección de Autor -->
        <div class="author-info">
            <img src="./images/logo.png" alt="Logo de hgaruna, desarrollador web y escritor técnico" width="90" height="90" loading="eager">
            <div class="author-details">
                <h4 class="page-break-avoid">hgaruna</h4>
