        }
        h1 { font-size: 2.2em; }
        h2 { font-size: 1.8em; }
        h3 { font-size: 1.4em; }
        p { margin-bottom: 1em; }
        ul {
            list-style-type: disc;
            margin-left: 20px;
            margin-bottom: 1em;
        }
        ul li { margin-bottom: 0.5em; }
        code {
            font-family: 'Courier New', Courier, monospace;
            background-color: #f0f0f0;
            padding: 0.2em 0.4em;
            border-radius: 3px;
            font-size: 0.9em;
            color: #c7254e; /* Un color distintivo para el código inline */
        }
        pre {
            background: #f5f5f5;
            border: 1px solid #ddd;
            padding: 1em;
            overflow: auto; /* Para scroll si el código es muy largo */
            margin: 1em 0;
            border-radius: 5px;
            page-break-inside: avoid; /* Evita que los bloques de código se partan entre páginas */
        }
        pre code {
            display: block; /* Asegura que el código ocupe todo el ancho del pre */
            background: none;
            padding: 0;
            border-radius: 0;
            font-size: 0.85em; /* Un poco más pequeño para bloques de código */
