            border-radius: 5px; /* Ligeras esquinas redondeadas para las imágenes */
        }
        .author-section {
            display: flex;
            align-items: center;
            margin-top: 3em;
            padding-top: 1.5em;
            border-top: 1px solid #eee;
        }
        .author-section img {
            width: 90px;
            height: 90px;
            border-radius: 50%; /* Para hacer la imagen circular */
            margin: 0 1.5em 0 0;
            flex-shrink: 0; /* Evita que la imagen se encoja */
        }
        .author-info h4 {
            margin-top: 0;
            margin-bottom: 0.2em;
        }
        .author-info p {
            font-size: 0.9em;
            color: #666;
            margin-bottom: 0.5em;
        }
        .author-info .social-links a {
            color: #007bff; /* Color de enlace estándar */
